import numpy as np 
import base64
from io import BytesIO
from matplotlib.figure import Figure
import matplotlib.pyplot as plt

#displays house vs median on twin x axis via bar and line chart
def house_x_median(x1, x2, zipcode): 
    fig = Figure()
    years = np.arange(2000, 2026)
    ax1 = fig.subplots()
    ax1.set_facecolor('whitesmoke')
    fig.patch.set_facecolor('whitesmoke')
    fig.patch.set_linewidth(5) 
    fig.patch.set_edgecolor('black')
    
    #bar chart (Houses Sold)
    color_bar = 'tab:blue'
    ax1.set_xlabel('Year', fontsize=12)
    ax1.set_ylabel('Houses Sold', color=color_bar, fontsize=12)
    ax1.bar(years, x1, color=color_bar, alpha=0.6, width=0.6)
    ax1.tick_params(axis='y', labelcolor=color_bar)
    ax1.grid(axis='x', linestyle='-.', alpha=0.3) # Vertical grid helps align years

    #line chart (Median Price)
    ax2 = ax1.twinx()  
    color_line = 'tab:red'
    ax2.set_ylabel('Median Price ($)', color=color_line, fontsize=12)
    ax2.plot(years, x2, color=color_line, linewidth=2, marker='o', markersize=5)
    ax2.tick_params(axis='y', labelcolor=color_line)

    #title and size
    fig.suptitle(f'Volume vs. Price for {zipcode} Through (2000-2025)', fontsize=16)
    fig.set_size_inches(10, 5)
    fig.tight_layout()

    #img save
    buf = BytesIO()
    fig.savefig(buf, format="png")
    data = base64.b64encode(buf.getbuffer()).decode("ascii")
    return data


#displays all zipcodes and thier medians via heatmap 
def zipcodes_x_median(median, zipcodes): #!(2964: median_values) (114: zipcodes) (25: years)
    data = [median[i:i + 114] for i in range(0, len(median), 114)] 
    print(f"{zipcodes}\n-------\n{data}\n-------\nSAMPLE-1: {data[0]}\n \nSAMPLE-2: {data[1]}")

    fig = Figure() 
    years = np.arange(2000, 2026)
    ax = fig.subplots()
    im = ax.imshow(
        data, 
        cmap='YlOrBr', 
        aspect='auto',  
        origin='lower', 
        interpolation='nearest',
        vmin = 0, vmax=600000
        )
    fig.colorbar(im, label="Median Price in ($'s)")
    
    #Adding row and column labels
    ax.set_xticks(range(len(zipcodes)), labels=zipcodes, rotation=65, fontsize=8) #TODO: [FIX THIS] make able to read
    ax.set_yticks(range(len(years)), labels=years)

    #set title
    fig.set_size_inches(18, 6.5)
    ax.set_title('ZIPCODES AND THIER RESPECTIVE MEDIAN PRICE (2000-2025)')
    fig.tight_layout()

    #save file
    buf = BytesIO()
    fig.savefig(buf, format="png")
    data = base64.b64encode(buf.getbuffer()).decode("ascii")
    return data


